mapper.select:
select d.*, c.name as categoryName
from dish d
left join category c on d.category_id = c.id


        and d.name like concat('%',#{name},'%')


        and d.category_id = #{categoryId}


        and d.status = #{status}

select d.id as d_id, d.name as d_name, d.category_id as d_category_id, d.price as d_price, d.image as d_image, d.description as d_description, d.status as d_status, d.update_time as d_update_time,
c.name as c_name,
df.id as df_id, df.name as df_name, df.dish_id as df_dish_id, df.value as df_value from dish d left join category c on d.category_id = c.id left join dish_flavor df on d.id = df.dish_id where d.id = #{id}

mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select * from dish d


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}

select count(id) from dish


        and status = #{status}


        and category_id = #{categoryId}
